import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AlertConfiguration = () => {
  const [alertConfigs, setAlertConfigs] = useState([]);
  const [devices, setDevices] = useState([]);
  const [geofences, setGeofences] = useState([]);
  const [newAlert, setNewAlert] = useState({
    geofence_id: '',
    device_id: '',
    alert_type: 'enter'
  });

  useEffect(() => {
    fetchAlertConfigs();
    fetchDevices();
    fetchGeofences();
  }, []);

  const fetchAlertConfigs = async () => {
    try {
      const response = await axios.get('/api/geofences/alerts');
      setAlertConfigs(response.data);
    } catch (error) {
      console.error('Error fetching alert configurations:', error);
    }
  };

  const fetchDevices = async () => {
    try {
      const response = await axios.get('/api/devices');
      setDevices(response.data);
    } catch (error) {
      console.error('Error fetching devices:', error);
    }
  };

  const fetchGeofences = async () => {
    try {
      const response = await axios.get('/api/geofences');
      setGeofences(response.data);
    } catch (error) {
      console.error('Error fetching geofences:', error);
    }
  };

  const handleInputChange = (e) => {
    setNewAlert({ ...newAlert, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/geofences/alerts', newAlert);
      fetchAlertConfigs();
      setNewAlert({ geofence_id: '', device_id: '', alert_type: 'enter' });
    } catch (error) {
      console.error('Error creating alert configuration:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`/api/geofences/alerts/${id}`);
      fetchAlertConfigs();
    } catch (error) {
      console.error('Error deleting alert configuration:', error);
    }
  };

  return (
    <div className="alert-configuration">
      <h2>Alert Configuration</h2>
      <form onSubmit={handleSubmit}>
        <select
          name="device_id"
          value={newAlert.device_id}
          onChange={handleInputChange}
          required
        >
          <option value="">Select Device</option>
          {devices.map((device) => (
            <option key={device.id} value={device.id}>
              {device.name}
            </option>
          ))}
        </select>
        <select
          name="geofence_id"
          value={newAlert.geofence_id}
          onChange={handleInputChange}
          required
        >
          <option value="">Select Geofence</option>
          {geofences.map((geofence) => (
            <option key={geofence.id} value={geofence.id}>
              Geofence {geofence.id} ({geofence.type})
            </option>
          ))}
        </select>
        <select
          name="alert_type"
          value={newAlert.alert_type}
          onChange={handleInputChange}
          required
        >
          <option value="enter">Enter</option>
          <option value="exit">Exit</option>
        </select>
        <button type="submit">Add Alert Configuration</button>
      </form>
      <h3>Existing Alert Configurations</h3>
      <ul>
        {alertConfigs.map((config) => (
          <li key={config.id}>
            Device {config.device_id} - Geofence {config.geofence_id} - {config.alert_type}
            <button onClick={() => handleDelete(config.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AlertConfiguration;
